mapper.select:
select count(*)
from tb_cart_product
where cart_seq = #{cartSeq}
and cart_prod_seq in

    #{item}

mapper.update:
update tb_cart_product
set qty = #{quantity}
where cart_prod_seq = #{cartProdSeq};

mapper.select:
select cart_prod_seq from tb_cart_product where cart_prod_seq in
#{item}
and soldout_yn = 'y'

select cp.prod_cd, pi.curr_inv from tb_cart_product cp JOIN tb_product p ON cp.prod_cd = p.prod_cd join tb_product_inventory pi on cp.prod_cd = pi.prod_cd LEFT JOIN tb_product_option po ON cp.opt_seq = po.opt_seq where cp.cart_prod_seq in
#{item}
and cp.del_yn <> 'y' group by cp.prod_cd, pi.curr_inv having (pi.curr_inv - sum(cp.qty * IF(po.qty IS NULL, 1, po.qty))) < 0

select cp.prod_cd, cp.typ, cp.qty, p.name, p.cnsmr_prc, p.sale_prc
from tb_cart_product cp,
     tb_product p
where cp.cart_seq = #{cartSeq}
  and cp.prod_cd = p.prod_cd
  and cp.del_yn <> 'y'

SELECT *
FROM tb_cart_product
WHERE mbr_id = #{mbr_id} AND prod_cd = #{prod_cd}
AND opt_seq IS NULL
AND opt_seq = #{opt_seq}
